pragma solidity ^0.7.1;
pragma experimental ABIEncoderV2;
//SPDX-License-Identifier: UNLICENSED

import "./Libraries/SafeMath.sol";
import "./Libraries/BytesLib.sol";

import "./Interfaces/IErc223.sol";

import "./Interfaces/IShyftKycContract.sol";
import "./Interfaces/IShyftKycContractRegistry.sol";
import "./Interfaces/IBlockRewardDistributor.sol";
import "./Interfaces/IBlockRewardUpdater.sol";
import "./Interfaces/IShyftConduit.sol";
import "./Administrable.sol";

import "./ECRecovery.sol";
import "./Interfaces/IShyftConduit.sol";

/// @dev | Shyft Conduit
///      |
///      | conduit
///      | /knd(y)oot/
///      | 1. a channel for conveying water or other fluid.
///      |
///      | conterminous
///      | /kntrmns/
///      | 1. sharing a common boundary.
///      |
///      | This contract manages the Shyft inflation organization and delegation to an Administrable distribution by value.
///      | Because the block reward never changes (only the difficulty parameter), there is no really complex math involved.
///      |
///      | We use linked lists for the Conterminous linkages because in the case of a new array, the list can be easily
///      | associated with the basic mappings without creating extra storage copies.
///      | Contextually, any Conduit formed by means of a network inflation is unable to be laundered through any means other
///      | than 100% network control. Network inflation is the purest of commodities, requiring physical resource production
///      | to "game", making it a much higher fruit than existing means.

//@note:@todo:@test: make comprehensive tests for this, validating the total distribution.

contract ShyftConduit is Administrable, IErc223ReceivingContract, IBlockRewardDistributor, IBlockRewardUpdater, IShyftConduit {
//    event EVT_conterminousInputValuesEqualsKeccakHash(  address[] conterminousAddresses,
//                                                        uint256[] conterminousDistributionPercentages_leftShifted24Bits,
//                                                        uint256 conterminousGeneration,
//                                                        bytes32 distributionKeyKeccak,
//                                                        bytes32 referenceHash);
//    event EVT_showBlockReward(uint shyftBlockReward, uint inflationAmount);
//
//    event EVT_doIntegrate(bool shouldIntegrate);
//
//    event EVT_doUpdateStep( address _originalAddress,
//                            uint _inflationAmount,
//                            bool preUpdateBenefactorStepRequired_val,
//                            address preUpdateBenefactorExContractAddress_val,
//                            bytes preUpdateBenefactorExForwardedBytes_val);

    /// @dev Event for an conduit action that withdraws funds to itself only
    event EVT_conduitWithdrawnFunds(uint256 amount, uint256 availableBalance);

    /* ERC223 events */
    /// @dev Event for fallback function of erc223 tokens.
    event EVT_Erc223TokenFallback(address indexed _from, uint256 indexed _value, bytes _data);

//    //@note: required for tests from kyc contract send
//    event EVT_receivedNativeBalance(address _from, uint256 _value);

    /// @dev Event for other tokens' reversal of transfer.
    event EVT_otherTokensReverseTransfer(uint256 indexed tokenIndex, address indexed tokenContract, uint256 value);

    /// @dev Event integrating an individual's account.
    event EVT_integratedIndividual(address indexed conterminousAddress, uint256 whiteShyftedBalance, uint256 numBlocksProcessed);

    /// @dev Event for withdrawing to the user's own trusted account.
    event EVT_withdrawToTrustedAccount(address indexed msgSender, address indexed shyftKycAddress, uint256 value);

    /// @dev event for withdrawing to native.
    event EVT_withdrawToNative(address indexed msgSender, address indexed to, uint256 value);

    /// @dev event for withdrawing to an external contract.
    event EVT_withdrawToExternalContract(address indexed msgSender, address indexed contractAddress, uint256 value);

    /// @dev using SafeMath library for uint256
    using SafeMath for uint256;

    /// @dev simple constant for a maximum 256 bit value.
    uint256 constant MAX_256 = 0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff;

    /// @dev ERC223 structs
    struct erc223Token {
        bool isActive;
        bytes4 sig;

        address originContract;
        address sender;
        uint value;
        bytes data;
    }

    /// @dev The array of erc223 token bundles.
    erc223Token[] public allErc223TokenBundles;

    /// @dev The current Shyft runtime inflation reward.
    uint constant runtimeInflationReward = 7.45814307 ether;
    /// @dev The current Shyft staking pool reward.
    uint constant stakingPoolReward = 7.45814307 ether;
    /// @dev The current Shyft block reward
    uint256 shyftBlockReward = runtimeInflationReward + stakingPoolReward;

    /// @dev Public address for the Shyft Distribution Contract address (block reward contract).
    address public systemDistributionContractAddress;
    /// @dev Public address for the Shyft Kyc Contract Registry address.
    address public shyftKycContractRegistryAddress;

    /// @dev Mutex lock, prevent recursion in functions that use external function calls
    bool locked;

    /// @dev Modifier that checks for array length equality.
    modifier checkAddressAndValuesArrayLengthsAreEqual(address[] memory _addresses, uint256[] memory _values) {
        require(_addresses.length == _values.length);
        _;
    }

    /// @dev Mapping for address to Conterminous account info.
    mapping(address => ConterminousInfo) allConterminousInfo;

    /// @dev total count for number of Contermious accounts.
    uint16 public conterminousCount = 0;

    /// @dev conterminous address for first of the forward linked list.
    address conterminousZerothAddress = address(0);
    /// @dev nonce for the "setConterminousDistributionAmounts" "_distributionKeyKeccak" input.
    uint256 conterminousGeneration;

    /// @dev Public Address for the Shyft Kyc Contract
    address public shyftKycContractAddress = address(0);

    /// @dev Public Address for the Prime Revocation Manager.
    address public primeRevocationManagerAddress = address(0);

    /// @dev Signature for triggering 'tokenFallback' in erc223 receiver contracts
    bytes4 constant shyftConduitSig = bytes4(keccak256("fromShyftConduit(address,address,uint256)")); // function signature

    /// @dev Signature from 'tokenFallback' sender in from kyc contract
    bytes4 constant shyftKycContractSig = bytes4(keccak256("fromShyftKycContract(address,address,uint256,uint256)")); // function signature

    /// @dev Kyc'd funds sent into this conduit manually.
    uint256 public manualKycFundsAdded;

    /// @dev External funds balance.
    /// @notice | There are three ways the Conduit can receive funds:
    ///      | 1) by proxy via the kyc contract it's linked to, which can be set up to only receive kyc'd funds.
    ///      | 2) by manual transfer of funds.
    ///      | 3) by the Shyft network inflation (or similar mechanisms).
    uint256 public externalFundsBalance;

    /// @dev the origin (first) block that this contract was updated (distributed) to.
    uint256 public originBlockNumber = MAX_256;

    /// @dev Initializes the Conduit with the contract owner as the sender.
    constructor() {
        owner = msg.sender;
    }

    /// @dev Gets the current shyft block reward.
    /// @return result the current shyft block reward.

    function getShyftBlockReward() public view returns (uint256 result) {
        return shyftBlockReward;
    }

    /// @dev Gets the number of Erc223 compatible token bundles that have been sent to this contract.
    /// @return result the number of Erc223 compatible token bundles that have been sent to this contract.

    function getNumErc223TokenBundles() public view returns (uint256 result) {
        return allErc223TokenBundles.length;
    }

    /// @param _systemDistributionContractAddress The smart contract address that the block reward is being distributed from.
    /// @dev | Sets the system distribution (block reward) contract that this conduit is receiving funds from.
    ///      | Uses Multi-signature permissions.
    /// @return result
    ///    | 0 = not an administrator
    ///    | 1 = not enough administrators have permissioned change
    ///    | 2 = system distribution contract address set

    function setSystemDistributionContractAddress(address _systemDistributionContractAddress) public override returns (uint8 result) {
        if (isAdministrator(msg.sender)) {
            bytes32 keyKeccak = keccak256(abi.encodePacked("setSystemDistributionContractAddress", _systemDistributionContractAddress));

            uint256 numPermissions = getPermissionsForMultisignKey(keyKeccak);

            bool permittedToModify;
            if (numPermissions >= adminConfirmationThreshold) {
                permittedToModify = true;
            } else {
                uint256 numConfirmedPermissions = adminApplyAndGetPermissionsForMultisignKey(keyKeccak);

                if (numConfirmedPermissions >= adminConfirmationThreshold) {
                    permittedToModify = true;
                }
            }

            if (permittedToModify == true) {
                systemDistributionContractAddress = _systemDistributionContractAddress;

                //@note:@here:@security if this function is ill-used by a
                // malicious admin, the worst they can do is reset the permissions
                // of the keys, and they have to be the one to execute a key
                // permissioning to get this far in the stack.
                adminResetPermissionsForMultisignKey(keyKeccak);

                //system distribution contract address set
                return 2;

            } else {
                //not enough administrators have permissioned change
                return 1;
            }
        } else {
            //not an administrator
            return 0;
        }
    }

    /// @param _shyftKycContractRegistryAddress The smart contract address of the Shyft Kyc Contract Registry.
    /// @dev | Sets the shyft kyc contract registry for purposes of making sure that trusted assets are routed across Trust Channels that only existing Shyft Kyc Contract (versions) can honour.
    ///      | Uses Multi-signature permissions.
    /// @return result
    ///    | 0 = not an administrator
    ///    | 1 = not enough administrators have permissioned change
    ///    | 2 = kyc contract registry address set

    function setShyftKycContractRegistryAddress(address _shyftKycContractRegistryAddress) public override returns (uint8 result) {
        if (isAdministrator(msg.sender)) {
            bytes32 keyKeccak = keccak256(abi.encodePacked("setShyftKycContractRegistryAddress", _shyftKycContractRegistryAddress));

            uint256 numPermissions = getPermissionsForMultisignKey(keyKeccak);

            bool permittedToModify;

            if (numPermissions >= adminConfirmationThreshold) {
                permittedToModify = true;
            } else {
                uint256 numConfirmedPermissions = adminApplyAndGetPermissionsForMultisignKey(keyKeccak);

                if (numConfirmedPermissions >= adminConfirmationThreshold) {
                    permittedToModify = true;
                }
            }

            if (permittedToModify == true) {
                shyftKycContractRegistryAddress = _shyftKycContractRegistryAddress;

                //@note:@here:@security if this function is ill-used by a
                // malicious admin, the worst they can do is reset the permissions
                // of the keys, and they have to be the one to execute a key
                // permissioning to get this far in the stack.
                adminResetPermissionsForMultisignKey(keyKeccak);

                //kyc contract registry address set
                return 2;

            } else {
                //not enough administrators have permissioned change
                return 1;
            }
        } else {
            //not an administrator
            return 0;
        }
    }

    /// @param _shyftKycContractAddress The smart contract address of the Shyft Kyc Contract.
    /// @dev | Sets the shyft kyc contract that will be used throughout the rest of the function calls.
    ///      | The Shyft Kyc Registry Contract address must be set before this function will call properly.
    ///      | Uses Multi-signature permissions.
    /// @return result
    ///    | 0 = not a valid kyc contract address
    ///    | 1 = not an administrator
    ///    | 2 = not enough administrators have permissioned change
    ///    | 2 = set kyc contract address

    function setShyftKycContractAddress(address _shyftKycContractAddress) public override returns (uint8 result) {
        if (IShyftKycContractRegistry(shyftKycContractRegistryAddress).isShyftKycContract(_shyftKycContractAddress) == true) {
            if (isAdministrator(msg.sender)) {
                bytes32 keyKeccak = keccak256(abi.encodePacked("setShyftKycContractAddress", _shyftKycContractAddress));

                uint256 numPermissions = getPermissionsForMultisignKey(keyKeccak);

                bool permittedToModify;

                if (numPermissions >= adminConfirmationThreshold) {
                    permittedToModify = true;
                } else {
                    uint256 numConfirmedPermissions = adminApplyAndGetPermissionsForMultisignKey(keyKeccak);

                    if (numConfirmedPermissions >= adminConfirmationThreshold) {
                        permittedToModify = true;
                    }
                }

                if (permittedToModify == true) {
                    shyftKycContractAddress = _shyftKycContractAddress;

                    //@note:@here:@security if this function is ill-used by a
                    // malicious admin, the worst they can do is reset the permissions
                    // of the keys, and they have to be the one to execute a key
                    // permissioning to get this far in the stack.
                    adminResetPermissionsForMultisignKey(keyKeccak);

                    //set kyc contract address
                    return 3;

                } else {
                    //not enough administrators have permissioned change
                    return 2;
                }
            } else {
                //not an administrator
                return 1;
            }
        } else {
            //not a valid kyc contract address
            return 0;
        }
    }

    //@note: this is a *secondary* input pool. it an be used to push and pull funds to a secondary account.
    // *normal native fuel transfer function*
    //@note:@todo:@critical: do we want to send these external funds back? maybe governance reserve bucket?
    receive() external payable {
        externalFundsBalance = externalFundsBalance.add(msg.value);
    }

    /// @param _recipient The public address of the recipient of the external funds.
    /// @dev | Sends external (sent to this contract re: receive function) funds to a specific address. *Only able to be done after this contract has been detached from a conduit flow*.
    ///      | Uses Multi-signature permissions.
    /// @return result
    ///    | 0 = not an administrator
    ///    | 1 = not enough administrators have permissioned change
    ///    | 2 = assets sent to recipient address

    function retrieveExternalFundsAfterDetach(address _recipient) public override returns (uint8 result) {
        require(checkSelfBalance() > 0);

        if (isAdministrator(msg.sender)) {
            bytes32 keyKeccak = keccak256(abi.encodePacked("retrieveExternalFundsAfterDetach", _recipient));

            uint256 numPermissions = getPermissionsForMultisignKey(keyKeccak);

            bool permittedToModify;

            if (numPermissions >= adminConfirmationThreshold) {
                permittedToModify = true;
            } else {
                uint256 numConfirmedPermissions = adminApplyAndGetPermissionsForMultisignKey(keyKeccak);

                if (numConfirmedPermissions >= adminConfirmationThreshold) {
                    permittedToModify = true;
                }
            }

            if (permittedToModify == true) {
                adminResetPermissionsForMultisignKey(keyKeccak);

                if (shyftBlockReward == 0) {
                    // use "call" with unbound gas, sending the entire balance of this contract to this recipient.
                    (bool success, ) = _recipient.call{value: address(this).balance}("");

                    // set "external" funds value to zero
                    externalFundsBalance = 0;

                    //revert if error, since this is a transactional event.
                    if (success == false) {
                        revert();
                    }

                    // assets sent to recipient address
                    return 3;
                } else {
                    // not currently detached
                    return 2;
                }
            } else {
                // not enough administrators have permissioned change
                return 1;
            }
        } else {
            // not an administrator
            return 0;
        }
    }

    /// @dev the mutex modifier.
    modifier mutex() {
        if (locked) revert();
        locked = true;
        _;
        locked = false;
    }

    /// @param _amount The amount of native tokens that the caller is making available to this Shyft Conduit instance.
    /// @dev | From an external Verified Shyft Kyc Contract (that has been set previously by administrators), move native fuel tokens from that verified contract's account balance that belongs to this Conduit instance.
    ///      | Used in the case of funds not being sent directly from a block reward instance.
    /// @notice This function does *not* need to be called when there is a block reward instance distributing native fuel tokens.
    /// @notice If the Kyc contract instance has ever been sent a message by this Conduit contract instance to have their account only accept Trusted assets, then this flow can be considered completely Kyc'd, as the Trust Channel routing will be enforced.
    /// @return result
    ///    | 0 = does not have available balance
    ///    | 1 = did not withdraw
    ///    | 2 = transfer successful

    function withdrawSafeFundsToSelfOnlyTrustRouted(uint256 _amount) mutex public override returns (uint8 result) {
        IShyftKycContract kycContract = IShyftKycContract(shyftKycContractAddress);

        uint256 nativeBip32X_type = kycContract.getNativeTokenType();

        uint256 availableBalance = kycContract.getBalanceBip32X(address(this), nativeBip32X_type);

        if (availableBalance >= _amount) {
            uint256 gasAmount = 30000;

            bool didWithdraw = kycContract.withdrawToExternalContract(address(this), availableBalance, gasAmount);

            if (didWithdraw) {
                emit EVT_conduitWithdrawnFunds(_amount, availableBalance);

                manualKycFundsAdded = manualKycFundsAdded.add(_amount);

                //transfer successful
                result = 2;
            } else {
                //did not withdraw
                result = 1;
            }
        } else {
            //does not have available balance
            result = 0;
        }
    }

    // ** ERC 223 functionality ** //

    /// @param _from The address that the token fallback is designating to receive assets.
    /// @param _value The value transferred.
    /// @param _data An external bytes mechanism to pass function signatures et al. to this fallback for processing.
    /// @dev This Erc223 token fallback mechanism associates assets to the proper address.
    /// @return ok the results from the function (should be "true", or else a revert condition)

    function tokenFallback(address _from, uint _value, bytes memory _data) public override returns (bool ok) {
        bytes4 tokenSig;

        //make sure we have enough bytes to determine a signature
        if (_data.length >= 4) {
            tokenSig = bytes4(uint32(bytes4(bytes1(_data[3])) >> 24) + uint32(bytes4(bytes1(_data[2])) >> 16) + uint32(bytes4(bytes1(_data[1])) >> 8) + uint32(bytes4(bytes1(_data[0]))));
        }

        //https://github.com/Dexaran/ERC223-token-standard/blob/Recommended/Receiver_Interface.sol
        /* tkn variable is analogue of msg variable of Ether transaction
        *  tkn.sender is person who initiated this token transaction   (analogue of msg.sender)
        *  tkn.value the number of tokens that were sent   (analogue of msg.value)
        *  tkn.data is data of token transaction   (analogue of msg.data)
        *  tkn.sig is 4 bytes signature of function
        *  if data of token transaction is a function execution
        */

        allErc223TokenBundles.push();
        erc223Token storage newToken = allErc223TokenBundles[allErc223TokenBundles.length - 1];

        newToken.isActive = true;
        newToken.originContract = msg.sender;
        newToken.sender = _from;
        newToken.value = _value;

        newToken.data = _data;
        newToken.sig = tokenSig;

        emit EVT_Erc223TokenFallback(_from, _value, _data);

        return true;
    }

//    function processFallbackToken(erc223Token storage newToken) internal {
//        //assume signature like:
//        // keccak256(abi.encodePacked("__revert__", msg.sender)
//        //
//        // process: 0 -> send back (reverse tx)
//        // process: 1 -> ...?
//
//        if (sigList[newToken.sig].isRegistered == true) {
//            if (sigList[newToken.sig].process == 0) {
//
//            }
//        }
//    }


    /// @param _tokenIndex The index for the return of erc223 tokens that were incorrectly sent to this address.
    /// @notice as the erc223 spec does not define return codes, I'm assuming that any contracts that care in the future about getting assets returned to them can use specific codes + lookup values to do the proper behaviour in a relatively plain-text manner, where you could have a lookup table of various assorted (common) functionalities.
    /// @dev This Erc223 token fallback mechanism associates assets to the proper address.
    /// @return result
    ///     | 0 = index is out of bounds
    ///     | 1 = token is not active
    ///     | 2 = msg.sender not permissioned to return tokens
    ///     | 3 = could not reverse token transfer
    ///     | 4 = successfully reversed token transfer

    function callTokenReverseTransfer(uint256 _tokenIndex) mutex public override returns (uint8 result) {
        if (_tokenIndex < allErc223TokenBundles.length) {
            erc223Token storage token = allErc223TokenBundles[_tokenIndex];

            if (token.isActive == true) {
                if (isAdministrator(msg.sender) || msg.sender == token.sender) {
                    IErc223 erc223Contract = IErc223(token.originContract);

//                    bytes memory revertBytesSig = BytesLib.fromBytes32(keccak256(abi.encodePacked("__revert__", address(this))));

                    bytes memory empty;

                    bool didTransfer = erc223Contract.transfer(token.sender, token.value, empty);

                    if (didTransfer == true) {
                        token.isActive = false;

                        emit EVT_otherTokensReverseTransfer(_tokenIndex, token.sender, token.value);

                        //reversed token transfer
                        result = 4;
                    } else {
                        //could not reverse token transfer
                        result = 3;
                    }
                } else {
                    //msg.sender not permissioned to return tokens
                    result = 2;
                }
            } else {
                //token is not active
                result = 1;
            }
        } else {
            //index is out of bounds
            result = 0;
        }
    }

    // ** Conterminous management ** //

    /// @param _distributionKeyKeccak The distribution key keccak that is being voted on for the Conterminous distributions.
    /// @dev | This function takes in a keccak hash of all of the values (n/100%) that are shifted to the left by 24. (2 ^ 24 - 1) = 16777215.
    ///      | this is to apply granularity to the distribution values of 1 in (2 ^ 24) wei equivalents.
    ///      | http://x86asm.net/articles/fixed-point-arithmetic-and-tricks/
    ///      | Uses Multi-signature permissions.
    /// @notice In the UI that would manage this component, the _distributionKeyKeccak should be the keccak256 of all of the addresses and distribution percentages, along with the current "conterminousGeneration", acting as a nonce.
    /// @return result
    ///    | 0 = not an administrator
    ///    | 1 = not enough administrators have permissioned change
    ///    | 2 = ready to set conterminous distributions

    function setConterminousDistributionAmountsPermissions(bytes32 _distributionKeyKeccak) public override returns (uint8 result) {
        if (isAdministrator(msg.sender)) {
            bytes32 keyKeccak = keccak256(abi.encodePacked("setConterminousDistributionAmounts", _distributionKeyKeccak));

            uint256 numPermissions = getPermissionsForMultisignKey(keyKeccak);

            bool permittedToModify;

            if (numPermissions >= adminConfirmationThreshold) {
                permittedToModify = true;
            } else {
                uint256 numConfirmedPermissions = adminApplyAndGetPermissionsForMultisignKey(keyKeccak);

                if (numConfirmedPermissions >= adminConfirmationThreshold) {
                    permittedToModify = true;
                }
            }

            if (permittedToModify == true) {
                //ready to set conterminous distributions
                return 2;
            } else {
                //not enough administrators have permissioned change
                return 1;
            }
        } else {
            //not an administrator
            return 0;
        }
    }

    /// @param _conterminousAddresses The public addresses to which the assigned distribution can be claimed.
    /// @param _conterminousDistributionPercentages_leftShifted24Bits The percentage for this public address to be assigned (50, 40, 10, etc.)
    /// @param _conterminousGeneration The conterminous generation (nonce).
    /// @param _distributionKeyKeccak The percentage for this public address to be assigned (50, 40, 10, etc.)
    /// @dev | This function takes in a keccak hash of all of the values, as well as the values themselves. It compares the hash of the values + the "generation" as a nonce, compares and returns whether it matches the reference.
    /// @return result
    ///    | true = hash matches keccak result
    ///    | false = does not match keccak result

    function conterminousInputValuesEqualsKeccakHash(address[] memory _conterminousAddresses,
                                                     uint256[] memory _conterminousDistributionPercentages_leftShifted24Bits,
                                                     uint256 _conterminousGeneration,
                                                     bytes32 _distributionKeyKeccak) pure internal returns (bool result) {
        bytes32 referenceHash = keccak256(abi.encodePacked(_conterminousAddresses, _conterminousDistributionPercentages_leftShifted24Bits, _conterminousGeneration));

//        emit EVT_conterminousInputValuesEqualsKeccakHash(_conterminousAddresses, _conterminousDistributionPercentages_leftShifted24Bits, _conterminousGeneration, _distributionKeyKeccak, referenceHash);
        if (referenceHash == _distributionKeyKeccak) {
            //hash matches keccak result
            return true;
        } else {
            //hash does not match keccak result
            return false;
        }
    }

    /// @param _conterminousAddresses The public addresses to which the assigned distribution can be claimed.
    /// @param _conterminousDistributionPercentages_leftShifted24Bits The percentage for this public address to be assigned (50, 40, 10, etc.)
    /// @param _distributionKeyKeccak The percentage for this public address to be assigned (50, 40, 10, etc.)
    /// @dev | This function takes in a keccak hash of all of the values, as well as the values themselves. It compares the hash of the values + the "generation" as a nonce, compares and returns whether it is approved. Then it compares the percentages and makes sure it all is <= 100%. Finally, it de-lists all existing addresses (with an integration beforehand), and then adds the new list in, within a linked-list structure.
    ///      | Uses Multi-signature permissions.
    /// @notice In the ui that would manage this component, the _distributionKeyKeccak should be the keccak256 of all of the addresses and distribution percentages, along with the current "conterminousGeneration", acting as a nonce.
    /// @return result
    ///    | 0 = all distribution percentages added together are over 100%
    ///    | 1 = conterminous integrations don't match
    ///    | 2 = more administrator confirmations required
    ///    | 3 = set conterminous distribution amounts

    function setConterminousDistributionAmounts(address[] memory _conterminousAddresses,
                                                uint256[] memory _conterminousDistributionPercentages_leftShifted24Bits,
//                                                uint256 _blockReward,
                                                bytes32 _distributionKeyKeccak) public override checkAddressAndValuesArrayLengthsAreEqual(_conterminousAddresses, _conterminousDistributionPercentages_leftShifted24Bits)
                                                                                returns (uint8 result) {

        bytes32 keyKeccak = keccak256(abi.encodePacked("setConterminousDistributionAmounts", _distributionKeyKeccak));

        uint256 totalPercentage_leftShifted24Bits;

        for (uint16 i = 0; i < _conterminousAddresses.length; i++) {
            totalPercentage_leftShifted24Bits = totalPercentage_leftShifted24Bits.add(_conterminousDistributionPercentages_leftShifted24Bits[i]);
        }

        if (totalPercentage_leftShifted24Bits <= (uint256(100) << 24)) {
            //check to make sure the keccak of the input values + the current conterminousGeneration as a nonce equals the distribution key keccak.
            if (conterminousInputValuesEqualsKeccakHash(_conterminousAddresses, _conterminousDistributionPercentages_leftShifted24Bits, conterminousGeneration, _distributionKeyKeccak)) {
                //check if distribution is over threshold
                if (isConfirmationsIsOverThreshold(getPermissionsForMultisignKey(keyKeccak)) == true) {
                    integrateAllConterminousDistributionsAndDeWhitelist();

                    for (uint16 j = 0; j < _conterminousAddresses.length; j++) {
                        ConterminousInfo storage conterminousInfo = allConterminousInfo[_conterminousAddresses[j]];

                        conterminousInfo.ownAddress = _conterminousAddresses[j];
                        conterminousInfo.whiteShyftedDistributionPercent_leftShifted24Bits = _conterminousDistributionPercentages_leftShifted24Bits[j];
                        conterminousInfo.whiteListed = true;
                        conterminousInfo.lastBlockProcessed = block.number - originBlockNumber;

                        //@note: create a linked list of all the conterminous linkages.
                        if (j < _conterminousAddresses.length - 1) {
                            conterminousInfo.nextLink = _conterminousAddresses[j + 1];
                        } else {
                            conterminousInfo.nextLink = address(0);
                        }
                    }

                    conterminousCount = uint16(_conterminousAddresses.length);
                    //@note: set first address for forward linked list.
                    conterminousZerothAddress = _conterminousAddresses[0];

                    conterminousGeneration++;

                    //                shyftBlockReward = _blockReward;
                    //
                    adminResetPermissionsForMultisignKey(keyKeccak);

                    // set conterminous distribution amounts
                    return 3;
                } else {
                    // more administrator confirmations required
                    return 2;
                }
            } else {
                // conterminous integrations don't match
                return 1;
            }
        } else {
            // all distribution percentages added together are over 100%
            return 0;
        }
    }

    /// @dev This de-lists all existing addresses (with an integration beforehand).
    function integrateAllConterminousDistributionsAndDeWhitelist() internal {
        address conterminousAddress = conterminousZerothAddress;

        for (uint16 i = 0; i < conterminousCount; i++) {
            ConterminousInfo storage conterminousInfo = allConterminousInfo[conterminousAddress];

            uint256 numBlocksToProcess = block.number - originBlockNumber;

            if (numBlocksToProcess > 0) {
                conterminousInfo.whiteShyftedBalance = conterminousInfo.whiteShyftedBalance.add(((conterminousInfo.whiteShyftedDistributionPercent_leftShifted24Bits * numBlocksToProcess * shyftBlockReward) >> 24) / 100);
            }

            conterminousInfo.whiteShyftedDistributionPercent_leftShifted24Bits = 0;

            conterminousInfo.whiteListed = false;

            conterminousAddress = conterminousInfo.nextLink;

            conterminousInfo.nextLink = address(0);
        }
    }


    /// @dev This integrates all of the distributions for the existing conterminous addresses.
    /// @notice It's in everyone's best interest to run this function (or the "integrateIndividual" one) every block, on the Shyft blockchain there is specific block reward function that does this integration to a single degree downwards.
    /// @return result
    ///    | 0 = nothing to process
    ///    | 1 -> n = n blocks processed
    function integrateAllConterminousDistributions() internal returns (uint256 result) {
        uint256 highestNumberOfBlocksProcessed = 0;

        address conterminousAddress = conterminousZerothAddress;

        for (uint16 i = 0; i < conterminousCount; i++) {
            ConterminousInfo storage conterminousInfo = allConterminousInfo[conterminousAddress];

            uint256 numBlocksToProcess = block.number - originBlockNumber - conterminousInfo.lastBlockProcessed;

            if (numBlocksToProcess > 0) {
                conterminousInfo.whiteShyftedBalance = conterminousInfo.whiteShyftedBalance.add(((conterminousInfo.whiteShyftedDistributionPercent_leftShifted24Bits * numBlocksToProcess * shyftBlockReward) >> 24) / 100);

                conterminousInfo.lastBlockProcessed = block.number - originBlockNumber;

                if (conterminousInfo.lastBlockProcessed > highestNumberOfBlocksProcessed) {
                    highestNumberOfBlocksProcessed = conterminousInfo.lastBlockProcessed;
                }
            }

            conterminousAddress = conterminousInfo.nextLink;
        }

        return highestNumberOfBlocksProcessed;
    }

    /// @dev This integrates all of the distributions for the existing conterminous addresses, but *only* for the previous blocks.
    /// @notice It's in everyone's best interest to run this function (or the "integrateIndividual" one) every block, on the Shyft blockchain there is specific block reward function that does this integration to a single degree downwards.
    /// @return result
    ///    | 0 = nothing to process
    ///    | 1 -> n = n blocks processed
    function integrateAllConterminousDistributionsOnlyPreviousBlock() internal returns (uint256 result) {
        uint256 highestNumberOfBlocksProcessed = 0;

        address conterminousAddress = conterminousZerothAddress;

        for (uint16 i = 0; i < conterminousCount; i++) {
            ConterminousInfo storage conterminousInfo = allConterminousInfo[conterminousAddress];

            //checks for underflow because this could theoretically be called block 0.
            int256 numBlocksToProcess = int256(block.number) - int256(1) - int256(originBlockNumber) - int256(conterminousInfo.lastBlockProcessed);

            if (numBlocksToProcess > 0) {
                conterminousInfo.whiteShyftedBalance = conterminousInfo.whiteShyftedBalance.add(((conterminousInfo.whiteShyftedDistributionPercent_leftShifted24Bits * uint256(numBlocksToProcess) * shyftBlockReward) >> 24) / 100);

                conterminousInfo.lastBlockProcessed = block.number - originBlockNumber;

                if (conterminousInfo.lastBlockProcessed > highestNumberOfBlocksProcessed) {
                    highestNumberOfBlocksProcessed = conterminousInfo.lastBlockProcessed;
                }
            }

            conterminousAddress = conterminousInfo.nextLink;
        }

        return highestNumberOfBlocksProcessed;
    }

    /// @param _conterminousAddress The public address of the Conterminous account.
    /// @dev This integrates all of the distributions for this specific Conterminous address.
    /// @return result
    ///    | 0 = nothing to process
    ///    | 1 -> n = n blocks processed
    function integrateIndividualConterminousDistribution(address _conterminousAddress) public override returns (uint256 result) {
        ConterminousInfo storage conterminousInfo = allConterminousInfo[_conterminousAddress];
        uint256 numBlocksToProcess = block.number - originBlockNumber - conterminousInfo.lastBlockProcessed;

        if (conterminousInfo.whiteShyftedDistributionPercent_leftShifted24Bits > 0 && numBlocksToProcess > 0) {
            conterminousInfo.whiteShyftedBalance = conterminousInfo.whiteShyftedBalance.add(((conterminousInfo.whiteShyftedDistributionPercent_leftShifted24Bits * numBlocksToProcess * shyftBlockReward) >> 24) / 100);

            conterminousInfo.lastBlockProcessed = block.number - originBlockNumber;

            emit EVT_integratedIndividual(_conterminousAddress, conterminousInfo.whiteShyftedBalance, numBlocksToProcess);

            // n blocks processed
            return numBlocksToProcess;
        } else {
            // nothing to process
            return 0;
        }
    }

    //@note:
    // so without triggering the updateStep function (generally through "reward" in a block reward contract)
    // there's a situation like the following possible:
    //    153 cur block
    //    140 origin block
    //    130 last block updated
    //  owed = 153 - 130 = 23
    //  cur diff = 153 - 140 = 13
    //
    // in this situation, it's a strange condition anyhow since an integrate would be called generally anyhow
    // regardless, it should only be distributed as the *current* diff, since that's all that can be
    // guaranteed to be there with regular contract functioning.
    //
    // assumptions: all accounts are fully integrated by the time this function is called.

    /// @dev this resets all of the conterminous "last block processed" to a relative time instead of an absolute time.

    function resetAllConterminousLastBlockProcessed() internal {
        address conterminousAddress = conterminousZerothAddress;

        for (uint16 i = 0; i < conterminousCount; i++) {
            ConterminousInfo storage conterminousInfo = allConterminousInfo[conterminousAddress];

            conterminousInfo.lastBlockProcessed = block.number - originBlockNumber;

            conterminousAddress = conterminousInfo.nextLink;
        }
    }

    /// @param _shyftKycContractAddress The public address of the shyft kyc contract.
    /// @param _value The value of the distribution that should be sent through this kyc contract address.
    /// @param _gasToSend The amount of gas to send to this contract (30,000 suffices for the current shyft kyc contract calls.)
    /// @dev this only withdraws to any registered ShyftKycContract. this function can *only* be called with the message sender receiving the balance in their Kyc Contract.
    /// @return result
    ///    | 0 = is not a registered kyc contract
    ///    | 2 = transfer was successful

    function withdrawIndividualConterminousDistributionToTrustedAccount(address _shyftKycContractAddress, uint256 _value, uint256 _gasToSend) mutex public override returns (uint8 result) {
        require(checkSelfBalance() >= _value);
        require(checkConterminousWhiteShyftedBalance(msg.sender) >= _value);

        IShyftKycContractRegistry kycContractRegistry = IShyftKycContractRegistry(shyftKycContractRegistryAddress);
        if (kycContractRegistry.isShyftKycContract(_shyftKycContractAddress)) {
            allConterminousInfo[msg.sender].whiteShyftedBalance = allConterminousInfo[msg.sender].whiteShyftedBalance.sub(_value);

            IShyftKycContract kycContract = IShyftKycContract(_shyftKycContractAddress);

            (bool success, ) = address(_shyftKycContractAddress).call{value: _value, gas: _gasToSend}("");

//            //@note: after the transfer above, this contract will have token rights associated with it, at which point
//            // it immediately transfers said rights to the message sender.

             if (success == true && kycContract.transfer(msg.sender, _value) == true) {

                 emit EVT_withdrawToTrustedAccount(msg.sender, _shyftKycContractAddress, _value);
                 //transfer was successful
                 result = 2;
             } else {
                //could not transfer across trust channel
                //@note: revert() because of transfers potentially upstream
                revert();
//                result = 1;
             }
        } else {
            //is not a registered kyc contract
            result = 0;
        }
    }

    /// @param _to The public address of the recipient.
    /// @param _value The value of the distribution that should be sent to this address.
    /// @dev This withdraws distribution from an existing Conterminous account to a regular payable public address.
    /// @return ok
    ///    | false = not a native account
    ///    | true = withdraw to native account successfu

    function withdrawIndividualConterminousDistributionToNormalAccount(address payable _to, uint256 _value) mutex public override returns (bool ok) {
        require(checkSelfBalance() >= _value);
        require(checkConterminousWhiteShyftedBalance(msg.sender) >= _value);
        uint codeLength;

        //retrieve the size of the code on target address, this needs assembly
        assembly {
            codeLength := extcodesize(_to)
        }

        //makes sure it's sending to a native (non-contract) address
        if (codeLength == 0) {
            allConterminousInfo[msg.sender].whiteShyftedBalance = allConterminousInfo[msg.sender].whiteShyftedBalance.sub(_value);

            _to.transfer(_value);

            emit EVT_withdrawToNative(msg.sender, _to, _value);

            //withdraw to native account successful
            ok = true;
        } else {
            ok = false;
            //not a native account
        }
    }

    /// @param _contractAddress The public address of the contract that assets are being transferred to.
    /// @param _value The value of the distribution that should be sent to this address.
    /// @dev This withdraws distribution from an existing Conterminous account to an external contract address, with a token fallback (erc223) address. 30,000 gas limit hardcoded.
    /// @notice it's assumed that there will be a mechanism where the receiver knows (& has whitelisted) this contract and the associated withdrawal function signature (shyftConduitSig) and will properly allocate assets based on that.
    /// @return ok
    ///    | false = not a contract address
    ///    | true = withdraw to contract successful

    function withdrawIndividualConterminousDistributionToExternalContract(address _contractAddress, uint256 _value) mutex public override returns (bool ok) {
        require(checkSelfBalance() >= _value);
        require(checkConterminousWhiteShyftedBalance(msg.sender) >= _value);
        uint codeLength;

        //retrieve the size of the code on target address, this needs assembly
        assembly {
            codeLength := extcodesize(_contractAddress)
        }

        //makes sure it's sending to a native (non-contract) address
        if (codeLength > 0) {
            allConterminousInfo[msg.sender].whiteShyftedBalance = allConterminousInfo[msg.sender].whiteShyftedBalance.sub(_value);

            (bool success, ) = _contractAddress.call{value: _value, gas: 30000}("");

            IErc223ReceivingContract receiver = IErc223ReceivingContract(_contractAddress);
            bool fallbackSuccess = receiver.tokenFallback(msg.sender, _value, abi.encodePacked(shyftConduitSig));

            if (success == true && fallbackSuccess == true) {
                emit EVT_withdrawToExternalContract(msg.sender, _contractAddress, _value);

                //withdraw to contract successful
                ok = true;
            } else {
                //@note:@here: needs revert() due to asset transactions already having occurred
                revert();
            }
        } else {
            //not a contract address
        }
    }

