    // ** prime revocation manager address management & retrieval ** //

    /// @param _primeRevocationManagerAddress The public address of the prime revocation manager.
    /// @dev | Sets the Prime Revocation Manager, the address with the ability to remove stale attestations.
    ///      | Uses Multi-signature permissions.
    /// @return result
    ///    | 0 = not an administrator
    ///    | 1 = not enough administrators have permissioned change
    ///    | 2 = prime revocation manager set

    function setPrimeRevocationManagerAddress(address _primeRevocationManagerAddress) public override returns (uint8 result) {
        require(_primeRevocationManagerAddress != address(0));

        if (isAdministrator(msg.sender)) {
            bytes32 keyKeccak = keccak256(abi.encodePacked("primeRevocationManagerAddress", _primeRevocationManagerAddress));

            uint256 numPermissions = getPermissionsForMultisignKey(keyKeccak);

            bool permittedToModify;

            if (numPermissions >= adminConfirmationThreshold) {
                permittedToModify = true;
            } else {
                uint256 numConfirmedPermissions = adminApplyAndGetPermissionsForMultisignKey(keyKeccak);

                if (numConfirmedPermissions >= adminConfirmationThreshold) {
                    permittedToModify = true;
                }
            }

            if (permittedToModify == true) {
                primeRevocationManagerAddress = _primeRevocationManagerAddress;

                adminResetPermissionsForMultisignKey(keyKeccak);

                // prime revocation manager set
                return 2;

            } else {
                // not enough administrators have permissioned change
                return 1;
            }
        } else {
            // not an administrator
            return 0;
        }
    }

    /// @param _primeRevocationManagerAddress The public address of the potential prime revocation manager.
    /// @dev Gets whether the input address is actually the prime revocation manager.
    /// @return result
    ///    | false = is not prime revocation manager address
    ///    | true = is prime revocation manager address

    function isPrimeRevocationManager(address _primeRevocationManagerAddress) public override view returns (bool result) {
        if (primeRevocationManagerAddress == _primeRevocationManagerAddress) {
            //is prime revocation manager address
            return true;
        } else {
            //is not prime revocation manager address
            return false;
        }
    }

    // ** Block Reward Distributor functionality ** //

    /// @dev variable that controls whether the conterminous distributions integrate on an update call (from the block reward contract).
    bool public integrateConterminousDistributionsOnUpdate;

    // @note: "updateStep" from the interface, run by Shyft nodes once per block at no block cost.
    // @note:@todo This contract as-is has no need of a governance pass to update this contract currently

    /// @param _originalAddress The original address that was used for the conterminous distribution cycle.
    /// @param _inflationAmount The incoming inflation amount (should match up with local understanding)
    /// @dev | This function (called by the block reward system-owned distribution contract) updates the current state, we also have admin controls that are triggered here to update the benefactor (target of the system distribution contract) to another.
    ///      | Openethereum style block reward return calls are used.
    ///      | There is also a Conterminous system-wide integration call performed, which can be a functionality set or not by the administrators of this contract.
    /// @notice This function should only ever be called by the block reward contract, if it exists.
    /// @return success the success result of this function.
    /// @return preUpdateBenefactorStepRequired whether the benefactor update step is required
    /// @return preUpdateBenefactorExContractAddress what address the update step needs to call
    /// @return preUpdateBenefactorExForwardedBytes what, if any, forwarded bytes need to be sent to that other contract

    function updateStep(address _originalAddress, uint _inflationAmount) external override returns (bool success,
                                                                                                   bool preUpdateBenefactorStepRequired,
                                                                                                   address preUpdateBenefactorExContractAddress,
                                                                                                   bytes memory preUpdateBenefactorExForwardedBytes) {
        if (msg.sender == systemDistributionContractAddress) {
            if (_inflationAmount != shyftBlockReward) {
                //@note: integrates all *previous* blocks, and then sets the block reward to the new value.
                integrateAllConterminousDistributionsOnlyPreviousBlock();
                shyftBlockReward = _inflationAmount;
            }

            // set origin block if first updateStep called
            if (originBlockNumber == MAX_256) {
                integrateAllConterminousDistributions();

                originBlockNumber = block.number;

                resetAllConterminousLastBlockProcessed();
            }

            if (integrateConterminousDistributionsOnUpdate == true) {
                //@note: must integrate on conterminous distribution
                integrateAllConterminousDistributions();
//                    emit EVT_doIntegrate(true);
            }

            bool preUpdateBenefactorStepRequired_val = false;
            address preUpdateBenefactorExContractAddress_val = address(0);
            bytes memory preUpdateBenefactorExForwardedBytes_val = "";

            if (adminShouldUpdateBenefactor == true &&
                (adminShouldRemoveBenefactorPermanently == true || adminUpdateBenefactorAddressTarget != address(0))) {

                preUpdateBenefactorStepRequired_val = true;
                preUpdateBenefactorExContractAddress_val = address(this);

                //@note: the format for these looks like:
                //        bytes memory returnBytes;
                //        bytes1 testBytes1 = 0x2a;
                //        bytes memory preUpdateBenefactorExForwardedBytes_val = abi.encode(preUpdateBenefactorExContractAddress_val, testBytes1);

                preUpdateBenefactorExForwardedBytes_val = "";
            }

//            emit EVT_doUpdateStep(_originalAddress,
//                                  _inflationAmount,
//                                  preUpdateBenefactorStepRequired_val,
//                                  preUpdateBenefactorExContractAddress_val,
//                                  preUpdateBenefactorExForwardedBytes_val);

            return (true,
                    preUpdateBenefactorStepRequired_val,
                    preUpdateBenefactorExContractAddress_val,
                    preUpdateBenefactorExForwardedBytes_val);
        } else {
            return (true,
                    false,
                    address(0),
                    "");
        }
    }

    /// @param _shouldIntegrateOnDistributionOnUpdate Whether the conterminous addresses should be integrated on an update step.
    /// @dev | This function votes on the topic of whether to integrate the distributions on an update step (triggered from the block reward contract).
    ///      | Uses Multi-signature permissions.
    /// @return result
    ///    | 0 = not an administrator
    ///    | 1 = not enough administrators have permissioned change
    ///    | 2 = set whether the conterminous distributions should integrate on an update step

    function voteAdminShouldIntegrateConterminousDistributionsOnUpdate(bool _shouldIntegrateOnDistributionOnUpdate) public override returns (uint8 result) {
        if (isAdministrator(msg.sender)) {
            bytes32 keyKeccak = keccak256(abi.encodePacked("voteAdminShouldIntegrateConterminousDistributionsOnUpdate", _shouldIntegrateOnDistributionOnUpdate));

            uint256 numPermissions = getPermissionsForMultisignKey(keyKeccak);

            bool permittedToModify;

            if (numPermissions >= adminConfirmationThreshold) {
                permittedToModify = true;
            } else {
                uint256 numConfirmedPermissions = adminApplyAndGetPermissionsForMultisignKey(keyKeccak);

                if (numConfirmedPermissions >= adminConfirmationThreshold) {
                    permittedToModify = true;
                }
            }

            if (permittedToModify == true) {

                integrateConterminousDistributionsOnUpdate = _shouldIntegrateOnDistributionOnUpdate;

                //set whether the conterminous distributions should integrate on an update step
                return 2;
            } else {
                //not enough administrators have permissioned change
                return 1;
            }
        } else {
            //not an administrator
            return 0;
        }
    }


